import { createSlice } from "@reduxjs/toolkit";

const cartSlice=createSlice({
    name: "cart",
    initialState: {
        items: [],
        totalQuantity: 0
    },
    reducers: {
        addItemToCart(state, action) {
            const newItem = action.payload
            const existingItem = state.items.find(function (item) { return item.id === newItem.id })
            if (!existingItem) {
                state.items.push({id: newItem.id, price: newItem.price, quantity: 1, totalPrice: newItem.price, name:newItem.title})
            }
            else{
                existingItem.quantity++;
                existingItem.totalPrice += newItem.price;
            }
        },
        removeItemFromCart(state,action){
            const id=action.payload
            const existingItem=state.items.find(function(item){return item.id===id})
            if(existingItem.id===1){
                state.items=state.items.filter(function(item){return item.id!==id})
            }
            else{
                existingItem.quantity--;
                existingItem.totalPrice=existingItem.totalPrice-existingItem.price
            }
        }
    }
})

export const cartActions=cartSlice.actions
export default cartSlice
