import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';
import { useSelector} from 'react-redux/es/hooks/useSelector';
import {  useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { uiActions } from './store/ui-slice';

function App() {
  const dispatch=useDispatch()
  const showCart = useSelector(function (state) {
    return state.ui.cartIsVisible
  })

  const cart = useSelector(function (state) { return state.cart })
  const notification=useSelector(function(state){return state.ui.notification})

  useEffect(function () {
    const sendCartData = async function () {
      dispatch(uiActions.showNotification({status:"pending",title:"Sending...",message:"Sending cart data!"}))
      await fetch("https://react-http-c7367-default-rtdb.europe-west1.firebasedatabase.app/cart.json", {
        method: "PUT",
        body: JSON.stringify(cart)
      })
      if (!Response.ok) {
        throw new Error("Sending cart data failed.")
      }
      dispatch(uiActions.showNotification({status:"success",title:"Success",message:"Sending cart data success!"}))
    }

    sendCartData().catch(function(err){dispatch(uiActions.showNotification({status:"error",title:"Error...",message:"Sending cart data failed!"}))})
  }, [cart,dispatch])
  return (
    <>
    <Layout>
      {showCart && <Cart />}
      <Products />
    </Layout>
    </>
  );
}

export default App;
