import { createSlice } from "@reduxjs/toolkit";
import { uiActions } from "./ui-slice";

const cartSlice = createSlice({
    name: "cart",
    initialState: {
        items: [],
        totalQuantity: 0
    },
    reducers: {
        replaceCart(state, action) {
            state.totalQuantity = action.payload.totalQuantity;
            state.items = action.payload.items;
          },
        addItemToCart(state, action) {
            const newItem = action.payload
            const existingItem = state.items.find(function (item) { return item.id === newItem.id })
            state.totalQuantity++
            if (!existingItem) {
                state.items.push({ id: newItem.id, price: newItem.price, quantity: 1, totalPrice: newItem.price, name: newItem.title })
            }
            else {
                existingItem.quantity++
                existingItem.totalPrice = existingItem.totalPrice + newItem.price
            }
        },
        removeItemFromCart(state, action) {
            const id = action.payload
            console.log(action.payload);
            const existingItem = state.items.find(function (item) { return item.id === id })
            state.totalQuantity--
            if (existingItem.quantity === 1) {
                state.items = state.items.filter(function (item) { return item.id !== id })
            }
            else {
                existingItem.quantity--
                existingItem.totalPrice = existingItem.totalPrice - existingItem.price
            }
        }
    }
})


const sendCartData=function(cart){
    return async function(dispatch){
        dispatch(uiActions.showNotification({ status: "pending", title: "Sending...", message: "Sending cart data!" }))
        const response=await fetch("https://react-http-c7367-default-rtdb.europe-west1.firebasedatabase.app/cart.json", {
            method: "PUT",
            body: JSON.stringify(cart)
          })
          if (!response.ok) {
            throw new Error("Sending cart data failed.")
          }
          dispatch(uiActions.showNotification({ status: "success", title: "Success", message: "Sending cart data success!" }))
    }
}

export const cartActions = cartSlice.actions
export default cartSlice
